class CursosGrado extends BaseCursosGrado
{
    private $grado = NULL;
    private $entidadEducativa = NULL;
    
    function __construct($table = null, $isNewEntry = false) {
        parent::__construct($table, $isNewEntry);
    }
    public function getGrado(){
        if(is_null($this->grado)){
            $gee = GradoEntidadEducativaTable::getGradoEntidad(array('id_grado' => $this->getIdGrado(), 'id_entidad_educativa' => $this->getIdEntidadEducativa()));
            if($gee){
                $this->grado = $gee->getGrado();
            }
        }
        return $this->grado;
    }
    public function getEntidadEducativa(){
        if(is_null($this->entidadEducativa)){
            $gee = GradoEntidadEducativaTable::getGradoEntidad(array('id_grado' => $this->getIdGrado(), 'id_entidad_educativa' => $this->getIdEntidadEducativa()));
            if($gee){
                $this->entidadEducativa = $gee->getEntidadEducativa();
            }
        }
        return $this->entidadEducativa;
    }
    public function getNombre(){
        return $this->getGrado()->getAbreviatura().'-'.$this->getCurso()->getAbreviatura();
    }
}
